    private void btn_loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_loginActionPerformed
        if(txt_email.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar email");
               txt_email.requestFocus();
               return;
        }
         if(txt_contraseña.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar contraseña");
               txt_contraseña.requestFocus();
               return;
        }
         
        String email = txt_email.getText();
        String contraseña = new String(txt_contraseña.getPassword());
        String tipoUsuario = (String) comboBoxTipoUsuario.getSelectedItem();
        
        if( tipoUsuario.equalsIgnoreCase("Administrador")){
                tipoUsuario="ADMIN";
                 boolean res=controlador.validarCredenciales(email, contraseña, tipoUsuario);

                if(res==true){
                        JOptionPane.showMessageDialog(null,"Ingreso correcto");
                        VistaAdministrador vista=new VistaAdministrador();
                        vista.setVisible(true);
                }
                else{
                    JOptionPane.showMessageDialog(null,"Error revise email y password");
                }
        }
        if( tipoUsuario.equalsIgnoreCase("Cliente")){
                tipoUsuario="CLIENT";
                   boolean res=controlador.validarCredenciales(email, contraseña, tipoUsuario);

                if(res==true){
                        JOptionPane.showMessageDialog(null,"Ingreso correcto");
                        VistaCliente vista=new VistaCliente();
                        vista.setVisible(true);
                }
                else{
                    JOptionPane.showMessageDialog(null,"Error revise email y password");
                }
        }
        
      
    }//GEN-LAST:event_btn_loginActionPerformed

