    private void btnCrearUsuarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearUsuarioActionPerformed
        if(txtDni.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar dni");
               txtDni.requestFocus();
               return;
        }
        if(txtNombres.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar nombres");
               txtNombres.requestFocus();
               return;
        }
        if(txtApellidos.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar apellidos");
               txtApellidos.requestFocus();
               return;
        }
        if(txtDireccion.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar direccion");
               txtDireccion.requestFocus();
               return;
        }
        if(txtTelefono.getText().length()==0){
                JOptionPane.showMessageDialog(null, "Ingresar telefono");
               txtTelefono.requestFocus();
               return;
        }
        
        String dni,nombres,apellidos,direccion,telefono,email,contraseña;
        dni=txtDni.getText();
        nombres=txtNombres.getText();
        apellidos=txtApellidos.getText();
        direccion=txtDireccion.getText();
        telefono=txtTelefono.getText();
         email=txtEmail.getText();
         contraseña=txtContraseña.getText();
         
        Controlador controlador=new Controlador();
        controlador.insertarDatos(dni, nombres, apellidos, direccion, telefono, email, contraseña);
        
    }//GEN-LAST:event_btnCrearUsuarioActionPerformed

